mapper.update:
update `t_sys_user` set organId=#{organId}, loginName=#{loginName}, realName=#{realName}, mobile=#{mobile}, sex=#{sex}, birthday=#{birthday}, email=#{email}, officeTel=#{officeTel}, homeTel=#{homeTel}, status=#{status}, dimissionStatus=#{dimissionStatus}, dimissionTime=#{dimissionTime},
password=#{password},
updateTime=now() where id=#{id}

mapper.select:
select id
from t_sys_user
WHERE id=#{id}
      and password = #{password}

select 
from t_sys_user a 
where a.loginName=#{loginName}

    and a.id!=#{userId}
